import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useAuth } from '../contexts/AuthContext';
import EmailVerification from './EmailVerification';

// US Universities data
const US_UNIVERSITIES = [
  "Arizona State University",
  "Boston University",
  "Brown University",
  "California Institute of Technology (Caltech)",
  "Carnegie Mellon University",
  "Columbia University",
  "Cornell University",
  "Dartmouth College",
  "Duke University",
  "Emory University",
  "Georgetown University",
  "Georgia Institute of Technology",
  "Harvard University",
  "Indiana University Bloomington",
  "Johns Hopkins University",
  "Massachusetts Institute of Technology (MIT)",
  "Michigan State University",
  "New York University (NYU)",
  "North Carolina State University",
  "Northwestern University",
  "Ohio State University",
  "Pennsylvania State University",
  "Princeton University",
  "Purdue University",
  "Rice University",
  "Stanford University",
  "Texas A&M University",
  "University of Arizona",
  "University of California, Berkeley",
  "University of California, Davis",
  "University of California, Irvine",
  "University of California, Los Angeles (UCLA)",
  "University of California, San Diego",
  "University of California, Santa Barbara",
  "University of Chicago",
  "University of Colorado Boulder",
  "University of Florida",
  "University of Georgia",
  "University of Illinois at Urbana-Champaign",
  "University of Maryland",
  "University of Michigan",
  "University of Minnesota",
  "University of North Carolina at Chapel Hill",
  "University of Notre Dame",
  "University of Pennsylvania",
  "University of Pittsburgh",
  "University of Southern California (USC)",
  "University of Texas at Austin",
  "University of Virginia",
  "University of Washington",
  "University of Wisconsin-Madison",
  "Vanderbilt University",
  "Virginia Tech",
  "Washington University in St. Louis",
  "Yale University"
];

interface PasswordStrength {
  score: number;
  feedback: string[];
  isValid: boolean;
}

const SignUp: React.FC = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [university, setUniversity] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showVerification, setShowVerification] = useState(false);
  const [showUniversityDropdown, setShowUniversityDropdown] = useState(false);
  const [universitySearch, setUniversitySearch] = useState('');
  const { signUp } = useAuth();
  const dropdownRef = useRef<HTMLDivElement>(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setShowUniversityDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const validatePassword = (password: string): PasswordStrength => {
    const feedback: string[] = [];
    let score = 0;

    // Check minimum length
    if (password.length >= 8) {
      score += 25;
    } else {
      feedback.push('At least 8 characters');
    }

    // Check for capital letter
    if (/[A-Z]/.test(password)) {
      score += 25;
    } else {
      feedback.push('At least one capital letter');
    }

    // Check for number
    if (/\d/.test(password)) {
      score += 25;
    } else {
      feedback.push('At least one number');
    }

    // Check for special character
    if (/[!@#$%^&*(),.?":{}|<>]/.test(password)) {
      score += 25;
    } else {
      feedback.push('At least one special character');
    }

    return {
      score,
      feedback,
      isValid: score === 100
    };
  };

  const getPasswordStrengthColor = (score: number) => {
    if (score >= 75) return 'bg-green-50';
    if (score >= 50) return 'bg-yellow-50';
    if (score >= 25) return 'bg-orange-500';
    return 'bg-red-50';
  };

  const getPasswordStrengthText = (score: number) => {
    if (score >= 75) return 'Strong';
    if (score >= 50) return 'Medium';
    if (score >= 25) return 'Weak';
    return 'Very Weak';
  };

  const passwordStrength = validatePassword(password);

  // Filter universities based on search
  const filteredUniversities = US_UNIVERSITIES.filter(uni =>
    uni.toLowerCase().includes(universitySearch.toLowerCase())
  );

  const handleUniversitySelect = (selectedUniversity: string) => {
    setUniversity(selectedUniversity);
    setUniversitySearch(selectedUniversity);
    setShowUniversityDropdown(false);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (!university) {
      setError('Please select your university');
      setLoading(false);
      return;
    }

    if (!passwordStrength.isValid) {
      setError('Please meet all password requirements');
      setLoading(false);
      return;
    }

    if (password !== confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }

    try {
      await signUp(name, email, password, university);
      setShowVerification(true);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (showVerification) {
    return (
      <EmailVerification 
        email={email} 
        onBack={() => setShowVerification(false)} 
      />
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center px-4 py-8" style={{ backgroundColor: '#FAFAF8' }}>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="w-full max-w-md mx-auto text-center"
      >
        {/* Brand Name */}
        <motion.h1
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.1 }}
          className="text-3xl font-bold mb-8"
          style={{ color: '#046C4E', fontFamily: 'Inter' }}
        >
          OnlySwap
        </motion.h1>

        {/* Main Heading */}
        <motion.h2
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          className="text-2xl font-bold mb-2"
          style={{ color: '#046C4E', fontFamily: 'Inter' }}
        >
          Create Account
        </motion.h2>

        {/* Sub-heading */}
        <motion.p
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.3 }}
          className="text-base mb-8"
          style={{ color: '#046C4E', fontFamily: 'Inter' }}
        >
          Join us today
        </motion.p>

          <motion.form
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            onSubmit={handleSubmit}
            className="space-y-6"
          >
            {/* Full Name Input */}
            <div>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                placeholder="Full Name"
                required
                autoComplete="off"
                data-form-type="other"
                style={{ fontFamily: 'Inter' }}
              />
            </div>

            {/* Email Input */}
            <div>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                placeholder="Email"
                required
                autoComplete="off"
                data-form-type="other"
                style={{ fontFamily: 'Inter' }}
              />
            </div>

            {/* University Selection */}
            <div className="relative" ref={dropdownRef}>
              <input
                type="text"
                id="university"
                value={universitySearch}
                onChange={(e) => {
                  setUniversitySearch(e.target.value);
                  setShowUniversityDropdown(true);
                }}
                onFocus={() => setShowUniversityDropdown(true)}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                placeholder="Select your university"
                required
                autoComplete="off"
                data-form-type="other"
                style={{ fontFamily: 'Inter' }}
              />

              {showUniversityDropdown && (
                <motion.div
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  className="absolute z-50 w-full mt-1 bg-white border border-gray-300 rounded-lg shadow-lg max-h-60 overflow-y-auto"
                >
                  {filteredUniversities.length > 0 ? (
                    filteredUniversities.map((uni, index) => (
                      <button
                        key={index}
                        type="button"
                        onClick={() => handleUniversitySelect(uni)}
                        className="w-full px-4 py-3 text-left hover:bg-gray-50 transition-colors border-b border-gray-100 last:border-b-0"
                        style={{ fontFamily: 'Inter' }}
                      >
                        {uni}
                      </button>
                    ))
                  ) : (
                    <div className="px-4 py-3 text-gray-500" style={{ fontFamily: 'Inter' }}>
                      No universities found
                    </div>
                  )}
                </motion.div>
              )}
            </div>

            {/* Password Input */}
            <div>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                placeholder="Password"
                required
                autoComplete="new-password"
                data-form-type="other"
                style={{ fontFamily: 'Inter' }}
              />
            </div>

            {/* Confirm Password Input */}
            <div>
              <input
                type="password"
                id="confirmPassword"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                placeholder="Confirm Password"
                required
                autoComplete="new-password"
                data-form-type="other"
                style={{ fontFamily: 'Inter' }}
              />
            </div>

            {error && (
              <motion.div
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                className="text-red-600 text-sm text-center bg-red-50 p-3 rounded-lg"
                style={{ fontFamily: 'Inter' }}
              >
                {error}
              </motion.div>
            )}

            {/* Create Account Button */}
            <button
              type="submit"
              disabled={loading || !passwordStrength.isValid || password !== confirmPassword || !university}
              className="w-full py-3 px-4 rounded-lg font-semibold transition-colors"
              style={{ 
                backgroundColor: '#046C4E', 
                color: 'white', 
                fontFamily: 'Inter',
                opacity: (loading || !passwordStrength.isValid || password !== confirmPassword || !university) ? 0.7 : 1
              }}
            >
              {loading ? (
                <motion.div
                  animate={{ rotate: 360 }}
                  transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                  className="w-5 h-5 border-2 border-white border-t-transparent rounded-full mx-auto"
                />
              ) : (
                'Create Account'
              )}
            </button>
          </motion.form>

          {/* Sign In Link */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.6 }}
            className="mt-8 text-center space-y-2"
          >
            <p style={{ color: '#046C4E', fontFamily: 'Inter' }}>
              Already have an account?
            </p>
            <Link 
              to="/signin" 
              className="block"
              style={{ color: '#046C4E', fontFamily: 'Inter', textDecoration: 'underline' }}
            >
              Sign in here
            </Link>
          </motion.div>
        </motion.div>
      </div>
  );
};

export default SignUp;
