import React, { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

interface CreateListingForm {
  title: string;
  description: string;
  price: number;
  category: string;
  condition: 'new' | 'like-new' | 'good' | 'fair';
  images: File[];
}

const CreateListing: React.FC = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [formData, setFormData] = useState<CreateListingForm>({
    title: '',
    description: '',
    price: 0,
    category: '',
    condition: 'good',
    images: []
  });

  const categories = [
    { id: 'textbooks', name: 'Textbooks', icon: '📚' },
    { id: 'electronics', name: 'Electronics', icon: '💻' },
    { id: 'dorm-essentials', name: 'Dorm Essentials', icon: '🏠' },
    { id: 'clothing', name: 'Clothing', icon: '👕' },
    { id: 'furniture', name: 'Furniture', icon: '🪑' },
    { id: 'science', name: 'Science', icon: '🔬' },
    { id: 'fitness', name: 'Fitness', icon: '💪' }
  ];

  const conditions = [
    { value: 'new', label: 'New', description: 'Brand new, never used' },
    { value: 'like-new', label: 'Like New', description: 'Used once or twice, looks new' },
    { value: 'good', label: 'Good', description: 'Used but in good condition' },
    { value: 'fair', label: 'Fair', description: 'Used with some wear' }
  ];

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'price' ? parseFloat(value) || 0 : value
    }));
  };

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    if (files.length > 0) {
      setFormData(prev => ({
        ...prev,
        images: [...prev.images, ...files].slice(0, 5) // Limit to 5 images
      }));
    }
  };

  const removeImage = (index: number) => {
    setFormData(prev => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // TODO: Implement actual API call to create listing
      console.log('Creating listing:', formData);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Navigate back to seller dashboard
      navigate('/marketplace');
    } catch (error) {
      console.error('Failed to create listing:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const isFormValid = formData.title.trim() && 
                     formData.description.trim() && 
                     formData.price > 0 && 
                     formData.category && 
                     formData.images.length > 0;

  return (
    <div className="min-h-screen" style={{ backgroundColor: '#FAFAF8' }}>
      {/* Header */}
      <header className="bg-white/95 backdrop-blur-md border-b border-gray-200">
        <div className="max-w-4xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate('/marketplace')}
                className="p-2 rounded-full hover:bg-gray-100 transition-colors"
              >
                <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>
              <h1 
                className="text-2xl font-bold"
                style={{ color: '#046C4E', fontFamily: 'Inter' }}
              >
                Create New Listing
              </h1>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-4xl mx-auto px-4 py-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="bg-white rounded-3xl shadow-xl p-8"
        >
          <form onSubmit={handleSubmit} className="space-y-8">
            {/* Basic Information */}
            <div>
              <h2 
                className="text-xl font-bold mb-6"
                style={{ color: '#046C4E', fontFamily: 'Inter' }}
              >
                Basic Information
              </h2>

              <div className="space-y-6">
                <div>
                  <label 
                    htmlFor="title"
                    className="block text-sm font-semibold mb-2"
                    style={{ color: '#046C4E', fontFamily: 'Inter' }}
                  >
                    Product Title *
                  </label>
                  <input
                    type="text"
                    id="title"
                    name="title"
                    value={formData.title}
                    onChange={handleInputChange}
                    placeholder="e.g., iPhone 14 Pro, Calculus Textbook"
                    className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                    style={{ fontFamily: 'Inter' }}
                    required
                  />
                </div>

                <div>
                  <label 
                    htmlFor="description"
                    className="block text-sm font-semibold mb-2"
                    style={{ color: '#046C4E', fontFamily: 'Inter' }}
                  >
                    Description *
                  </label>
                  <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleInputChange}
                    placeholder="Describe your item in detail..."
                    rows={4}
                    className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent resize-none"
                    style={{ fontFamily: 'Inter' }}
                    required
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label 
                      htmlFor="price"
                      className="block text-sm font-semibold mb-2"
                      style={{ color: '#046C4E', fontFamily: 'Inter' }}
                    >
                      Price ($) *
                    </label>
                    <input
                      type="number"
                      id="price"
                      name="price"
                      value={formData.price}
                      onChange={handleInputChange}
                      placeholder="0.00"
                      min="0"
                      step="0.01"
                      className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                      style={{ fontFamily: 'Inter' }}
                      required
                    />
                  </div>

                  <div>
                    <label 
                      htmlFor="category"
                      className="block text-sm font-semibold mb-2"
                      style={{ color: '#046C4E', fontFamily: 'Inter' }}
                    >
                      Category *
                    </label>
                    <select
                      id="category"
                      name="category"
                      value={formData.category}
                      onChange={handleInputChange}
                      className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
                      style={{ fontFamily: 'Inter' }}
                      required
                    >
                      <option value="">Select a category</option>
                      {categories.map(category => (
                        <option key={category.id} value={category.id}>
                          {category.icon} {category.name}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>

                <div>
                  <label 
                    className="block text-sm font-semibold mb-3"
                    style={{ color: '#046C4E', fontFamily: 'Inter' }}
                  >
                    Condition *
                  </label>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
                    {conditions.map(condition => (
                      <label
                        key={condition.value}
                        className={`relative cursor-pointer p-4 rounded-xl border-2 transition-all duration-200 ${
                          formData.condition === condition.value
                            ? 'border-green-500 bg-green-50'
                            : 'border-gray-200 hover:border-gray-300'
                        }`}
                      >
                        <input
                          type="radio"
                          name="condition"
                          value={condition.value}
                          checked={formData.condition === condition.value}
                          onChange={handleInputChange}
                          className="sr-only"
                        />
                        <div className="text-center">
                          <div 
                            className="font-semibold mb-1"
                            style={{ color: '#046C4E', fontFamily: 'Inter' }}
                          >
                            {condition.label}
                          </div>
                          <div 
                            className="text-xs"
                            style={{ color: '#666666', fontFamily: 'Inter' }}
                          >
                            {condition.description}
                          </div>
                        </div>
                      </label>
                    ))}
                  </div>
                </div>
              </div>
            </div>

            {/* Image Upload */}
            <div>
              <h2 
                className="text-xl font-bold mb-6"
                style={{ color: '#046C4E', fontFamily: 'Inter' }}
              >
                Product Images *
              </h2>

              <div className="space-y-4">
                <div
                  onClick={() => fileInputRef.current?.click()}
                  className="border-2 border-dashed border-gray-300 rounded-xl p-8 text-center cursor-pointer hover:border-green-500 hover:bg-green-50 transition-all duration-200"
                >
                  <div className="text-4xl mb-4">📸</div>
                  <p 
                    className="text-lg font-semibold mb-2"
                    style={{ color: '#046C4E', fontFamily: 'Inter' }}
                  >
                    Upload Product Images
                  </p>
                  <p 
                    className="text-sm"
                    style={{ color: '#666666', fontFamily: 'Inter' }}
                  >
                    Click to upload up to 5 images (PNG, JPG, JPEG)
                  </p>
                </div>

                <input
                  ref={fileInputRef}
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="hidden"
                />

                {/* Image Preview */}
                {formData.images.length > 0 && (
                  <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
                    {formData.images.map((image, index) => (
                      <div key={index} className="relative group">
                        <img
                          src={URL.createObjectURL(image)}
                          alt={`Preview ${index + 1}`}
                          className="w-full h-32 object-cover rounded-xl"
                        />
                        <button
                          type="button"
                          onClick={() => removeImage(index)}
                          className="absolute top-2 right-2 w-6 h-6 bg-red-500 text-white rounded-full flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-200"
                        >
                          ×
                        </button>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* Submit Button */}
            <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200">
              <button
                type="button"
                onClick={() => navigate('/marketplace')}
                className="px-6 py-3 rounded-xl font-semibold border border-gray-300 transition-all duration-200 hover:bg-gray-50"
                style={{ 
                  color: '#666666',
                  fontFamily: 'Inter'
                }}
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={!isFormValid || isSubmitting}
                className={`px-8 py-3 rounded-xl font-semibold text-white transition-all duration-200 ${
                  isFormValid && !isSubmitting
                    ? 'hover:shadow-lg'
                    : 'opacity-50 cursor-not-allowed'
                }`}
                style={{ 
                  backgroundColor: '#046C4E',
                  fontFamily: 'Inter'
                }}
              >
                {isSubmitting ? (
                  <div className="flex items-center space-x-2">
                    <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full"></div>
                    <span>Creating...</span>
                  </div>
                ) : (
                  'Create Listing'
                )}
              </button>
            </div>
          </form>
        </motion.div>
      </div>
    </div>
  );
};

export default CreateListing;
